import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api from './service/api';
import ReactLoading from 'react-loading';


const PostPage = (props) => {
  const [post, setPost] = useState(null);

  useEffect(() => {
    async function loadPost(){
      await new Promise(resolve => setTimeout(resolve, 5000))
      let result = await api.get(
        `/api/v2/pages/${props.id}/`
      )
      setPost(result.data)
    }

    if(!post)
      loadPost();
  })

  return(
    <div id = 'main'>
      {(post &&
        <div id = 'post'>
          <img
            src = {`http://localhost:8000${post.post_image.url}`}
            width = '100%'
            height = {post.post_image.height}
          />
          <h1>{post.title}</h1>

          <div dangerouslySetInnerHTML={{
            __html: post.body
          }} />
        </div>
      )||
        <div id = 'loading'>
          <ReactLoading type = {'spokes'} color = {'#000'} height={200} width={100}/>
        </div>
      }
    </div>
  )
}


class Main extends React.Component{
    constructor(props){
      super(props);
      this.state = {};
    }

    componentDidMount(){
      this.loadData();
    }

    loadData = async () => {
      try{
        let result = await api.get(
          '/api/v2/pages/?type=posts.PostPage&fields=post_image&order=-id'
        );

        let firstItem = result.data.items[0];
        result.data.items.shift();

        this.setState({
          firstItem,
          list: result.data.items,
        });
      }catch(e){
        console.log(e)
      }
    }

    handleClick = (id) => {
      this.setState({
        openPostPage: id
      })
    }

    organizePostRows = (list) => {
      var content = []

      for(let i = 0; i < list.length ; i+=2){

        let row = {leftItem: list[i]};
        if(list[i+1]) row.rightItem = list[i+1];
        content.push(row);

      }
      return content;
    }

    render(){
      var { firstItem, list, openPostPage } = this.state;

      console.log(openPostPage)

      if(list)
        var postList = this.organizePostRows(list);


      return(
        <div id = 'wrapper'>

          <div id = 'top-bar'> 
            <h1>RADIOHIPERATIVO</h1>
          </div>


          {list && !openPostPage &&
            <div id = 'main'> 
              <div 
                id = 'head-image-container'
                onClick = {() => this.handleClick(firstItem.id)}
              >
                <img 
                  src = {`http://localhost:8000${firstItem.post_image.url}`}
                  width = '100%'
                  height =  {firstItem.post_image.height}
                  className = 'image'
                />

                <p>
                  {firstItem.title}
                </p>

              </div>

              <div id='post-list'> 
                {postList.map((item, index) => (
                  <div 
                    className = 'post-row'
                    key = {index}
                  >
                    <div 
                      className = 'image-container-left'
                      onClick = {() => this.handleClick(item.leftItem.id)}
                    >
                      <img
                        src = {`http://localhost:8000${item.leftItem.post_image.url}`}
                        width = '100%'
                        height = {item.leftItem.post_image.height / 2} 
                        className = 'image'
                      />
                      <p>{item.leftItem.title}</p>
                    </div>

                    <div 
                      className = 'image-container-right'
                      onClick = {() => this.handleClick(item.rightItem.id)}
                    >
                      <img
                        src = {`http://localhost:8000${item.rightItem.post_image.url}`} 
                        width = '100%'
                        height = {item.rightItem.post_image.height / 2}
                        className = 'image'
                      />
                      <p>{item.rightItem.title}</p>
                    </div>

                  </div>
                ))}
              </div>

            </div>   
           }

           {openPostPage && <PostPage id={openPostPage}/>}
        </div>
      )
    }
}

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
