import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api from './service/api';


class Main extends React.Component{
    constructor(props){
      super(props);
      this.state = {};
    }

    componentDidMount(){
      this.loadData();
    }

    loadData = async () => {
      try{
        let result = await api.get(
          '/api/v2/pages/?type=posts.PostPage&fields=post_image&order=-id'
        );

        let firstItem = result.data.items[0];
        result.data.items.shift();

        this.setState({
          firstItem,
          list: result.data.items
        });
      }catch(e){
        console.log(e)
      }
    }

    organizePostRows = (list) => {
      var content = []

      for(let i = 0; i < list.length ; i+=2){
        content.push({
          leftItem: list[i], 
          rightItem: list[i+1]
        })
      }

      console.log(content)
      return content;
    }

    render(){
      var { firstItem, list } = this.state;

      if(list)
        var postList = this.organizePostRows(list);


      return(
        <div >
          <h1>RADIOHIPERATIVO</h1>
          {list &&
            <div id = 'wrapper'> 
              <div id = 'head-image-container'>
                <img 
                  src = {`http://localhost:8000${firstItem.post_image.url}`}
                  width = '100%'
                  height =  {firstItem.post_image.height}
                  className = 'image'
                />

                <p>
                  {firstItem.title}
                </p>

              </div>

              <div className='post-list'> 

                {postList.map(item => {
                  <div className = 'post-row'>
                    <p>{item.leftItem.title}</p>
                    <p>{item.rightItem.title}</p>
                  </div>
                })}
              </div>

            </div>   
           }
        </div>
      )
    }
}

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
