import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import api from './service/api';



const PostPage = (props) => {
  return(
    <div id='main'>
      <h1 id='teste'>{props.id}</h1>
    </div>
  )
}


class Main extends React.Component{
    constructor(props){
      super(props);
      this.state = {};
    }

    componentDidMount(){
      this.loadData();
    }

    loadData = async () => {
      try{
        let result = await api.get(
          '/api/v2/pages/?type=posts.PostPage&fields=post_image&order=-id'
        );

        let firstItem = result.data.items[0];
        result.data.items.shift();

        this.setState({
          firstItem,
          list: result.data.items,
          openPostPage: undefined
        });
      }catch(e){
        console.log(e)
      }
    }

    handleClick = (id) => {
      this.setState({
        openPostPage: id
      })
    }

    organizePostRows = (list) => {
      var content = []

      for(let i = 0; i < list.length ; i+=2){

        let row = {leftItem: list[i]};
        if(list[i+1]) row.rightItem = list[i+1];
        content.push(row);

      }

      console.log(content)
      return content;
    }

    render(){
      var { firstItem, list, openPostPage } = this.state;

      if(list)
        var postList = this.organizePostRows(list);


      return(
        <div id = 'wrapper'>

          <div id = 'top-bar'> 
            <h1>RADIOHIPERATIVO</h1>
          </div>


          {list && !openPostPage &&
            <div id = 'main'> 
              <div 
                id = 'head-image-container'
                onClick = {this.handleClick(firstItem.id)}
              >
                <img 
                  src = {`http://localhost:8000${firstItem.post_image.url}`}
                  width = '100%'
                  height =  {firstItem.post_image.height}
                  className = 'image'
                />

                <p>
                  {firstItem.title}
                </p>

              </div>

              <div id='post-list'> 
                {postList.map(item => (
                  <div className = 'post-row'>
                    <div className = 'image-container-left'>
                      <img
                        src = {`http://localhost:8000${item.leftItem.post_image.url}`}
                        width = '100%'
                        height = {item.leftItem.post_image.height / 2} 
                        className = 'image'
                      />
                      <p>{item.leftItem.title}</p>
                    </div>

                    <div className = 'image-container-right'>
                      <img
                        src = {`http://localhost:8000${item.rightItem.post_image.url}`} 
                        width = '100%'
                        height = {item.rightItem.post_image.height / 2}
                        className = 'image'
                      />
                      <p>{item.rightItem.title}</p>
                    </div>

                  </div>
                ))}
              </div>

            </div>   
           }

           {openPostPage && <PostPage id={openPostPage}/>}
        </div>
      )
    }
}

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
